        self.x,self.y = pygame.mouse.get_pos()
        self.IsClick = pygame.mouse.get_pressed()

    def LeftClick(self):
        return self.IsClick[0]

    def MidClick(self):
        return self.IsClick[1]

    def RightClick(self):
        return self.IsClick[2]

    def GetPosition(self):
